def find_first_unique_substring(s, l=4):
    for i in range(len(s)-l):
        ss = s[i:i+l]
        if len(set(ss))==l:
            return i+l



assert find_first_unique_substring("bvwbjplbgvbhsrlpgdmjqwftvncz") == 5
assert find_first_unique_substring("nppdvjthqldpwncqszvftbrmjlhg") == 6
assert find_first_unique_substring("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg") == 10
assert find_first_unique_substring("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw") == 11


print(find_first_unique_substring(""))
print(find_first_unique_substring("",l=14))